from abc import ABC, abstractmethod

class AbstractVehicle(ABC):
    @abstractmethod
    def is_daily_driving(self):
        pass

class Car(AbstractVehicle):
    def is_daily_driving(self) -> bool:
        return True

class Motorcycle(AbstractVehicle):
    def is_daily_driving(self) -> bool:
        return True

class Bus(AbstractVehicle):
    def is_daily_driving(self) -> bool:
        return False

class Truck(AbstractVehicle):
    def is_daily_driving(self) -> bool:
        return False

def ispersonal(vehicle: AbstractVehicle) -> bool:
    if vehicle.is_daily_driving():
        return True
    else:
        return False

# Example
print(ispersonal(Car()))  # Output: True
print(ispersonal(Motorcycle()))  # Output: True
print(ispersonal(Bus()))  # Output: False
print(ispersonal(Truck()))  # Output: False
