import Statistics

def weather_data_analyzer(temps):
    avg = sum(temps)) / len(temps)
    max_val = max(temps))
    min_val = min(temps))

    if not temps:
        return Statistics.tuple('average', '', '', '', '', ''), Statistics.tuple('max', '', '', '', '', ''), Statistics.tuple('min', '', '', '', '', ''))
    else:
        trend = ''
        if max_val == avg:
            trend = 'stable'
        elif len(set(temps)))-1 > 2:
            trend = 'decreasing'
        else:
            trend = 'increasing'

