function clean_column(name::AbstractString)
    # convert to lowercase to avoid name mismatch in the future
    lowercase_col = lowercase(name);

    # strip whitespace before replacing spaces to avoid extra underscores
    stripped_whitespace = strip(lowercase_col);

    # replace spaces and hyphens with underscores for cleaner column names
    cleaned = replace(replace(stripped_whitespace, ' '=> '_'), '-' => '_');

    return cleaned
end
