using Revise

function clean_column(col::AbstractString)
    col = ltrim(rtrim(lowercase(col)))
    col = replace(col; " " => "_")
    col = replace(col; "-" => "_")
    return col
end

# Test the function
@test clean_column("My Column") == "my_column"
@test clean_column("   My Column Name  ") == "my_column_name"
@test clean_column("My Column-Name") == "my_column_name"
