/**
 * Extracts Julia code blocks from a markdown document.
 * It removes the code fences and joins multiple code blocks with a newline.
 * 
 * @paramÂ {String} md - The markdown document with Julia code blocks.
 * @returns {String} extracted Julia code blocks as a single string.
 */
const extract_julia_code = (md) => {
    // find all code blocks in markdown
    const codeBlockRegex = /```julia(.*?)```/gms;

    // find all codeblock matches
    const codeBlocks = [...md.matchAll(codeBlockRegex)].map(block => block[1].trim());

    // join the codeblocks with a newline
    const code = codeBlocks.join('\n');

    // return the code
    return code;
};
