function extract_julia_code(md::String)
  code_blocks = filter(is_julia_block, split(md, "\n"))
  return join(map(remove_code_fences, code_blocks), "\n")
end

function is_julia_block(s::String)
  return startswith(s, "```julia") || startswith(s, "```jl")
end

function remove_code_fences(block::String)
  v = split(block, "\n")
  return join(v, "\n")[3:end-2]
end
