class AbstractVehicle {}

class Car extends AbstractVehicle {}

class Motorcycle extends AbstractVehicle {}

class Bus extends AbstractVehicle {}

class Truck extends AbstractVehicle {}

class Speeder extends AbstractVehicle {} // Defaulting to false as the parent doesn't define isPersonal

function isPersonal(vehicle) {
    // For simplicity assume you have a property defining the purpose of a vehicle
    return (vehicle instanceof Car || vehicle instanceof Motorcycle);
}

console.log(isPersonal(new Car())); // True
console.log(isPersonal(new Motorcycle())); // True
console.log(isPersonal(new Bus())); // False
console.log(isPersonal(new Truck())); // False
console.log(isPersonal(new Speeder())); // False
