trait Driveable {
}

trait Vehicle {
}

struct Car;
struct Motorcycle;
struct Bus;
struct Truck;

fn is_driveable<T: Vehicle>(t: T) -> bool {
    match t {
        Car | Motorcycle => true,
        _ => false,
    }
}

fn main() {
    let car = Car;
    let motorcycle = Motorcycle;
    let bus = Bus;
    let truck = Truck;

    println!("Is car driveable? {}", is_driveable(car));
    println!("Is motorcycle driveable? {}", is_driveable(motorcycle));
    println!("Is bus driveable? {}", is_driveable(bus));
    println!("Is truck driveable? {}", is_driveable(truck));
}
```

