/**
 * This function filters a list of words to only include words that start with a capital letter.
 *
 * @param words A list of words to be filtered
 * @return A list of words that start with a capital letter
 */
function keep_only_names(words) {
    // Initialize an empty list to store the filtered words
    let filtered_words = [];

    // Iterate over each word in the provided list
    for (let word of words) {
        // Check if the first character of the word is uppercase
        if (word[0].toUpperCase() === word[0]) {
            // If the word starts with a capital letter, add it to the filtered list
            filtered_words.push(word);
        }
    }

    // Return the filtered list of words
    return filtered_words;
}
