using JuPy

function pig_latinify(strings)
    function transformer(str)
        first_consonant_idx = findfirst(!='a', str; firstidx=true)
        if isnothing(first_consonant_idx)
            return string(str, "way")
        else
            return string(str[first_consonant_idx:end], str[1:first_consonant_idx-1], "ay")
        end
    end
    map(transformer, strings)
end

pig_latinify(["hello", "apple", "strong", "world"])|>println
