using Threads

function pig_latinify(words::Vector{String})
  threads = Threads.map(words,  (word) ->
    pig_latin(word)
  )
  return Threads.collect(threads)
end

function pig_latin(word::String)
  (if first(word) in "aeiouAEIOU"
    word . "yay"
  else
    for i in 1:length(word)
      if first(Substring(word, i:end-1)) in "aeiouAEIOU"
        return Substring(word, i:end) . "ay" . Substring(word, 1:i-1)
      end
    end
    word . "way"
  end)
end

output = pig_latinify(["hello", "apple", "strong", "world"])
println(output) 
