using Markdown

function q_and_a_extractor(md::AbstractString)
    content = parse(md)
    blocks = [bl for bl in content.blocks]

    qa = [
        (strip(bl.content_string), strip(bl2.content_string))
        for (bl, bl2) in zip(blocks[1:end:2], blocks[2:end:2])
        if bl.level == 2 && bl2.level == 2 && startswith(bl.content_string, "# Question")
    ]
    return qa
end
