function q_and_a_extractor(md::String)
    question_start = findfirst("# Question", md)
    answer_start = findfirst("# Answer", md)

    if isnothing(question_start) || isnothing(answer_start)
        return ("", "")
    end

    question = strip(md[question_start.stop+1:answer_start.start-1])
    answer = strip(md[answer_start.stop+1:end])

    return (question, answer)
end
