function timezone_bumper(dt::DateTime, bump::Int=3)
    return dt + Dates.Hour(bump)
end

# Test the function
@show timezone_bumper(DateTime(2023, 4, 10, 12, 0),3)  # returns 2023-04-10T15:00:00

# Test with default bump
@show timezone_bumper(DateTime(2023, 4, 10, 12, 0))  # returns 2023-04-10T15:00:00

# Test with Date type
@show timezone_bumper(Date(2023, 4, 10))  # throws a MethodError
```
