function wrap_string(s::String; text_width::Int=10)
    lines = ["$s[1:min(text_width, length(s))]];
    wstart = min(text_width, length(s))+1;
    while(wstart â‰¤ length(s) )
        wend = min(wstart + text_width -1, length(s));
        push!(lines, s[wstart:wend]);
        wstart += text_width;
    end
    return lines
end
