using Statistics

struct WeatherResults
    average::Union{Nothing, Number}
    max::Union{Nothing, Number}
    min::Union{Nothing, Number}
    trend::Union{Nothing, Symbol}
end

function weather_data_analyzer(temps)
    if isempty(temps)
        return WeatherResults(nothing, nothing, nothing, nothing)
    end

    avg = mean(temps)
    max_temp = maximum(temps)
    min_temp = minimum(temps)

    if isempty(temps)
        return WeatherResults(nothing, nothing, nothing, nothing)
    end

    if all(temps[2:end] .> temps[1:end-1])
        trend = :increasing
    elseif all(temps[2:end] .< temps[1:end-1])
        trend = :decreasing
    else
        trend = :stable
    end

    return WeatherResults(avg, max_temp, min_temp, trend)
end
