# Write the function add_yearmonth
function add_yearmonth(df::DataFrame)
    yr_mo = Dates.format.(df[:, :dt], "yymm") |> collect |> Vector{String}
    df[!, :ym] = yr_mo
    df
end

# Apply the function to the given DataFrame
df = add_yearmonth(df)

# Check that your solution is correct
@assert all(df[:, :ym] .== ["202005", "202101", "202212"])  # Your code should return true
