function clean_column(col)
    col = lowercase(col)
    col = strip(col)
    col = replace(col, " " => "_")
    col = replace(col, "-" => "_")
    return col
end

# Test the function
@assert clean_column("My Column") == "my_column"
@assert clean_column("Column with spaces ") == "column_with_spaces_"
@assert clean_column("Another-Column-Name") == "another_column_name"
