def q_and_a_extractor(md):
    # extract question and answer
    start = md.find('# Question') + len('# Question')
    end = md[start:].find('\n# ') + start
    question = md[start:end]

    start = md.find('# Answer') + len('# Answer')
    end = md[start:].find('\n# ') + start if '#' in md[start:] else len(md)
    answer = md[start:end]
    return (question,answer)
