def q_and_a_extractor(md):
    questions,answers=[],[]
    for line in md.split('\n'):
        if line[:10]=='# Question':
            questions.append(line[12:])
        elif line[:9]=='# Answer':
            answers.append(line[11:])
    return (questions,answers)
