function timezone_bumper(dt::DateTime, bump::Union{Tuple{}, Int}=3)
    if !ismissing(bump) && (typeof(bump) == Tuple || typeof(bump) == Int)
        return DateTime(Dates.value(dt) + Dates.value(Dates.hour(bump)) * 60^2 * 60^2)
    elseif ismissing(bump)
        return dt + 3hours()
    else
        error("Invalid bump type: must be an Int or a tuple of Ints.")
    end
end
