using Statistics

function weather_data_analyzer(temps)
    if isempty(temps)
        return (average=nothing, max=nothing, min=nothing, trend=nothing)
    end

    avg = mean(temps)
    max = maximum(temps)
    min = minimum(temps)

    # Calculate the slope of a line fitted to the temperatures to determine if there is an increasing or decreasing trend.
    fit_slope = Statistics.LinRegressor() \ [temps; zeros(length(temps))]
    trend = fit_slope > 0 ? :increasing : fit_slope < 0 ? :decreasing : :stable

    return (average=avg, max=max, min=min, trend=trend)
end
