function weather_data_analyzer(temps)
    if isempty(temps)
        return (average=nothing, max=nothing, min=nothing, trend=nothing)
    end

    statistics = Statistics.mean(temps);
    max = maximum(temps);
    min = minimum(temps);
    diffs = [abs(temps[i+1] - temps[i]) for i in 1:length(temps)-1];
    trend = ifelse((sum(diffs) > 0 && min >= max) || (sum(diffs) < 0 && min <= max), "stable", ((sum(diffs) > 0 && min <= max) ? "increasing" : "decreasing"))
    return (average=statistics, max=max, min=min, trend=trend)
end
